import { Dispatch, useState, useEffect, SetStateAction } from 'react'
import { Note } from '../logic/colllections/note'

export let notes: Note[]
export let setNotes: Dispatch<SetStateAction<Note[]>>

export function useNotes() {
  const asyncContainer = async () => {
    ;[notes, setNotes] = useState(await findNotes())
  }
  asyncContainer()
}

export async function findNotes(): Promise<Note[]> {
  return await (await fetch('api/notes')).json()
}
