import { MongoClient, Db, Collection } from 'mongodb'

const DB_ADDRESS_PORT = 'mongodb://localhost:27017'
const DB_NAME = 'mongodb'

export function insertDocument(collectionName: string, document: object) {
  getCollection(collectionName).insertOne(document)
}

export function findDocuments(
  collectionName: string,
  filter: object,
  reconnects?: boolean
): any[] {
  MongoClient.connect(DB_ADDRESS_PORT, (err, client) => {
    client
      .db(DB_NAME)
      .collection(collectionName)
      .find(filter)
      .toArray((err, docs) => console.log(docs))
    client.close()
  })
  return ['tet']
}

/**
 * @private
 */
function getCollection(collectionName: string): Collection<any> {
  let collection: Collection<any>
  MongoClient.connect(DB_ADDRESS_PORT, (err, client) => {
    collection = client.db(DB_NAME).collection(collectionName)
    client.close()
  })
  return collection
}
