import { MongoClient } from 'mongodb'

const URL = 'mongodb://localhost:27017'
const NAME = 'mongodb'

export async function insertDocument(
  collectionName: string,
  document: object
): Promise<void> {
  let client: MongoClient
  try {
    client = await MongoClient.connect(URL, { useNewUrlParser: true })
    await client.db(NAME).collection(collectionName).insertOne(document)
  } catch (error) {
    console.error(error)
  } finally {
    client.close()
  }
}

export async function findDocuments(
  collectionName: string,
  filter: object
): Promise<any[]> {
  let client: MongoClient
  try {
    client = await MongoClient.connect(URL, { useNewUrlParser: true })
    return await client.db(NAME).collection(collectionName).find({}).toArray()
  } catch (error) {
    console.error(error)
  } finally {
    client.close()
  }
}

/**
 * @private
 */
async function execute(action: Function) {
  let client: MongoClient
  try {
    client = await MongoClient.connect(URL, { useNewUrlParser: true })
    const db = client.db(NAME)
    return await action.call(db)
  } catch (error) {
    console.error(error)
  } finally {
    client.close()
  }
}
