import { playOpenSound } from '../util/sounds'
import { setReadMode } from '../logic/appMode'

export const ReadButton = () => (
  <button
    onClick={() => {
      playOpenSound()
      async function fetchNotes() {
        const res = await fetch('api/notes')
        const data = await res.json()
        setNotes(data)
      }
      fetchNotes()
      setReadMode()
    }}
  >
    Read
  </button>
)
