import { CSSProperties } from 'react'

export const center: CSSProperties = {
  position: 'absolute',
  left: '50%',
  top: '50%',
  transform: 'translate(-50%, -50%)',
}
export const horizontallyCenter: CSSProperties = {
  display: 'table',
  margin: '0 auto',
}

/**
 *
 * @param from range (%)
 * @param to  range (%)
 */
export const splitVertical = (from: number, to: number): CSSProperties => {
  return {
    top: `${from}%`,
    height: `${to - from}%`,
    width: '100%',
    position: 'fixed',
    left: 0,
    right: 0,
  }
}
