import { CSSProperties } from 'react'

export const center: CSSProperties = {
  position: 'absolute',
  left: '50%',
  top: '50%',
  transform: 'translate(-50%, -50%)',
}
export const horizontallyCenter: CSSProperties = {
  display: 'table',
  margin: '0 auto',
}

export const splitVertical: CSSProperties = {
  height: '50%',
  width: '100%',
  position: 'fixed',
  left: 0,
  right: 0,
}

export const splitVerticalTop: CSSProperties = {
  top: 0,
  backgroundColor: '#111',
}

export const splitVerticalMiddle: CSSProperties = {
  top: '45%',
  backgroundColor: 'yellow',
  bottom: '45%',
}

export const splitVerticalBottom: CSSProperties = {
  bottom: 0,
  backgroundColor: 'red',
}
