import { useState, useEffect } from 'react';
import { executeAsync } from '../logic/async';
export let notes;
export let setNotes;
export function useNotes() {
  ;
  [notes, setNotes] = useState([]);
  useEffect(loadNotes, []);
}

async function findNotes() {
  return await (await fetch('api/note')).json();
}

function loadNotes() {
  executeAsync(async () => setNotes(await findNotes()));
}

export function deleteNote(note) {
  fetch(`api/note`, {
    method: 'DELETE',
    body: JSON.stringify(note._id)
  });
  loadNotes();
}
export function addNote(src) {
  fetch('/api/note', {
    method: 'post',
    body: JSON.stringify({
      text: src
    })
  });
}
export function modifyNote(src, id) {
  fetch('/api/note', {
    method: 'put',
    body: JSON.stringify({
      text: src,
      id
    })
  });
}
