import { Note } from '../../logic/colllections/note';
export default (async (req, res) => {
  switch (req.method) {
    case 'get':
      res.status(200).json(await Note.find());
      return;

    case 'post':
      const text = JSON.parse(req.body).text;
      await new Note(text).insert();
      res.status(200).json({
        message: 'ok'
      });
      return;
  }
});
