function loadSoundLibrary() {
  useEffect(() => SOUND_FILE_NAMES.forEach(setAudio), []);
}

function setAudio(fileName) {
  soundLibrary[fileName] = new Audio(`sounds/${fileName}.mp3`);
}

function playAudio(fileName) {
  soundLibrary[fileName].play();
  setAudio(fileName);
