import { CSSProperties } from 'react'

export const center: CSSProperties = {
  position: 'absolute',
  left: '50%',
  top: '50%',
  transform: 'translate(-50%, -50%)',
}
export const horizontallyCenter: CSSProperties = {
  display: 'table',
  margin: '0 auto',
}

const splitVertical: CSSProperties = {
  height: '50%',
  width: '100%',
  position: 'fixed',
  left: 0,
  right: 0,
}

export const splitVerticalTop: CSSProperties = Object.assign(
  {
    top: 0,
    backgroundColor: '#111',
  },
  splitVertical
)

export const splitVerticalMiddle: CSSProperties = Object.assign(
  {
    top: '45%',
    backgroundColor: 'yellow',
    bottom: '45%',
  },
  splitVertical
)

export const splitVerticalBottom: CSSProperties = Object.assign(
  {
    bottom: 0,
    backgroundColor: 'red',
  },
  splitVertical
)
