import { useState } from 'react'
import ReactMarkdown from 'react-markdown'
import { Note } from '../logic/colllections/note'

interface Props {
  note: Note
}

export default (props: Props) => {
  const [collapsed, setCollaspsed] = useState(true)
  const collapsedText = props.note.text
    .split(/[\r\n]/)
    .filter((line, i) => i < 2)
    .reduce((l1, l2) => `${l1}\r\n${l2}`)
  return (
    <div onClick={() => setCollaspsed(!collapsed)}>
      <ReactMarkdown
        source={collapsed ? collapsedText : props.note.text}
      ></ReactMarkdown>
    </div>
  )
}
