import ReactMarkdown from 'react-markdown'
import { viewingNote } from '../states/viewingNote'

export const NoteViewer = () => (
  <div
    style={{
      position: 'absolute',
      left: '50%',
      transform: 'translate(-50%, 0%)',
    }}
  >
    {viewingNote ? (
      <ReactMarkdown source={viewingNote.text}></ReactMarkdown>
    ) : null}
  </div>
)
