import { useState } from 'react'
import ReactMarkdown from 'react-markdown'
import { Note } from '../logic/colllections/note'

interface Props {
  note: Note
}

export default (props: Props) => {
  const [collapsed, setCollaspsed] = useState(true)
  const collapsedText = props.note.text.substr(0, 50)
  return (
    <div onClick={() => setCollaspsed(!collapsed)}>
      <ReactMarkdown
        source={collapsed ? collapsedText : props.note.text}
      ></ReactMarkdown>
    </div>
  )
}
