import { useState, Dispatch, SetStateAction } from 'react'

const SOUND_FILE_NAMES = ['open', 'close', 'enter']

let soundLibrary: SoundLibrary
let setSoundLibrary: Dispatch<SetStateAction<SoundLibrary>>

export function useSounds(): void {
  const sounds = {}
  SOUND_FILE_NAMES.forEach(
    (fileName) => (sounds[fileName] = new Audio(`sounds/${fileName}.mp3`))
  )
  ;[soundLibrary, setSoundLibrary] = useState(sounds)
}

export function playOpenSound(): void {
  soundLibrary['open'].play()
}

export function playCloseSound(): void {
  soundLibrary['close'].play()
}

export function playEnterSound(): void {
  soundLibrary['enter'].play()
}

interface SoundLibrary {
  [soundName: string]: HTMLAudioElement
}
